  render(){
    return(
      <form className="fish-edit" onSubmit={this.createFish}>
        <input ref={nameRef} type="text"  name="name" placeholder="Name"/>
        <input ref={priceRef}type="text" name="price" placeholder="Price"/>
        <select ref={statusRef} type="text"name="status">
          <option value="available">Fresh!</option>
          <option value="unavailable">Sold!</option>
        </select>

        <textarea ref={descRef} type="text" name="desc" placeholder="Desc"/>
        <input ref={imageRef} type="text" name="image" placeholder="Image"/>
        <button type="submit" >+ Add Fish</button>
      </form>
    );
  }
}

export default AddFishForm;
